INSERT INTO `pizza` (`id`, `prezzo`, `nome`, `descrizione`, `url_foto`) VALUES (NULL, '8.50', 'Margherita', 'Una pizza classica con salsa di pomodoro, mozzarella fresca e basilico.', ''), (NULL, '10.50', 'Capricciosa', 'Una pizza ricca con salsa di pomodoro, mozzarella, funghi, olive nere, prosciutto cotto e carciofi.', ''), (NULL, '11.50', 'Quattro Stagioni', 'Una pizza suddivisa in quattro sezioni rappresentanti le quattro stagioni dell anno. Le sezioni sono generalmente condite con ingredienti come prosciutto cotto e funghi per l autunno, carciofi e olive nere per l inverno, pomodori e basilico per la primavera e olive verdi e peperoni per l estate.', ''), (NULL, '9.00', 'Diavola', 'Una pizza piccante con salsa di pomodoro, mozzarella e salame piccante.', ''), (NULL, '9.00', 'Caprese', 'Una pizza leggera con salsa di pomodoro, mozzarella, pomodori freschi a fette, basilico e olio d oliva.', ''), (NULL, '10.00', 'Prosciutto e Funghi', 'Una pizza con salsa di pomodoro, mozzarella, prosciutto cotto e funghi champignon.', '')
